//! TIP20Factory dispatch tests.
//!
//! The dispatch implementation is auto-generated by the `#[contract(abi, dispatch)]` macro.

#[cfg(test)]
mod tests {
    use crate::{
        storage::{StorageCtx, hashmap::HashMapStorageProvider},
        test_util::{assert_full_coverage, check_selector_coverage},
        tip20_factory::{ITIP20Factory::Calls, TIP20Factory},
    };

    #[test]
    fn tip20_factory_test_selector_coverage() {
        let mut storage = HashMapStorageProvider::new(1);

        StorageCtx::enter(&mut storage, || {
            let mut factory = TIP20Factory::new();

            let unsupported = check_selector_coverage(
                &mut factory,
                Calls::SELECTORS,
                "ITIP20Factory",
                Calls::name_by_selector,
            );

            assert_full_coverage([unsupported]);
        })
    }
}
