import * as React from 'react'
import { Link } from 'react-router'
import { usePostHogTracking } from '../lib/posthog'

export function HomePage() {
  const { trackCTAClick, trackExternalLinkClick } = usePostHogTracking()

  return (
    <div className="py-25 max-[470px]:py-14 px-6">
      <div className="fixed right-0 top-30 max-[1180px]:top-40 max-[970px]:top-60">
        <Vector className="h-[733px] max-[1180px]:h-[600px] max-[970px]:h-[400px] max-[780px]:hidden text-[var(--vocs-color_inverted)]/20" />
      </div>
      <div className="flex flex-col max-[470px]:items-center max-[470px]:text-center">
        <LogoLockup className="w-[148px] text-[var(--vocs-color_inverted)]" />
        <div className="h-[82px] max-[470px]:h-[40px]" />
        <div className="max-w-[410px]">
          <h1 className="text-[48px] max-[470px]:text-[32px] font-medium leading-[48px] max-[470px]:leading-[32px] tracking-tight text-[var(--vocs-color_inverted)]">
            The blockchain for payments at scale
          </h1>
          <div className="h-[16px]" />
          <p className="text-[17px] font-normal max-[470px]:font-[300] leading-[22px] tracking-normal text-[var(--vocs-color_inverted)]/80">
            Tempo is designed to be low-cost, fast, and secure. By dedicating
            capacity to payments, it delivers on the uptime and throughput
            demands of the real world.
          </p>
        </div>
        <div className="h-[24px]" />
        <div className="flex gap-[12px]">
          <Button
            variant="inverted"
            render={
              <Link
                to="/"
                onClick={() => trackCTAClick('Start Building', '/')}
              />
            }
          >
            Start Building
          </Button>
          <Button
            variant="inverted"
            render={
              <Link
                to="/documentation"
                onClick={() => trackCTAClick('Learn Tempo', '/documentation')}
              />
            }
          >
            Learn Tempo
          </Button>
          <Button
            externalLink
            render={
              // biome-ignore lint/a11y/useAnchorContent: _
              <a
                href="https://tempo.xyz"
                target="_blank"
                rel="noopener noreferrer"
                onClick={() => trackExternalLinkClick('https://tempo.xyz', 'Website')}
              />
            }
          >
            Website
          </Button>
        </div>
        <div className="h-[60px]" />
      </div>
    </div>
  )
}

// biome-ignore lint/correctness/noUnusedVariables: _
function Button(props: Button.Props) {
  const {
    children,
    externalLink = false,
    render = <button type="button" />,
    variant = 'default',
    ...rest
  } = props
  const className = [
    'flex items-center justify-center w-fit h-[32px] px-[14px] rounded-[7px] font-medium tracking-tight text-[14px]',
    Button.variantToClassName[variant],
    props.className,
  ]
    .filter(Boolean)
    .join(' ')

  return React.cloneElement(render, {
    ...rest,
    className,
    children: externalLink ? (
      <div className="flex items-center gap-2">
        {children}
        <div>â†—</div>
      </div>
    ) : (
      children
    ),
  })
}

namespace Button {
  export type Props = React.ButtonHTMLAttributes<HTMLButtonElement> & {
    externalLink?: boolean
    variant?: keyof typeof variantToClassName | undefined
    render?: React.ReactElement | undefined
  }

  export const variantToClassName = {
    default:
      'text-[var(--vocs-color_inverted)] bg-[var(--vocs-color_background)] border border-dashed',
    inverted:
      'bg-[var(--vocs-color_inverted)] text-[var(--vocs-color_background)] border dark:border-dashed',
  } as const
}

